import { useState } from "react";
import { useNavigate } from "react-router-dom";
function Add({ setName, setQuantity, setPrice, setDiscount, handlerAdd }) {
  // const DefaultPage = () => <p>Nothing to see here.</p>;

  const handlerSetName = (event) => {
    setName(event.target.value);
  };

  const handlerSetQuantity = (event) => {
    setQuantity(event.target.value);
  };

  const handlerSetPrice = (event) => {
    setPrice(event.target.value);
  };

  const handlerSetDiscount = (event) => {
    setDiscount(event.target.value);
  };
  // const handlerSubmitForm = () => {
  //   e.preventDefault();
  // };

  const navigate = useNavigate();
  return (
    <>
      <form>
        <label>Name:</label>
        <input
          type="text"
          name="name"
          onChange={handlerSetName}
          placeholder="Enter Product Name"
        ></input>
        <br />
        <label>Quantity:</label>
        <input
          type="text"
          name="quantity"
          onChange={handlerSetQuantity}
          placeholder="Enter Quantity"
        ></input>
        <br />
        <label>Price:</label>
        <input
          type="text"
          name="price"
          onChange={handlerSetPrice}
          placeholder="Enter Price"
        ></input>
        <br />
        <label>Discount:</label>
        <input
          type="text"
          name="discount"
          onChange={handlerSetDiscount}
          placeholder="Enter Discount"
        ></input>
        <br />
        <button
          onClick={(e) => {
            e.preventDefault();
            handlerAdd();
            // navigate("/view");
          }}
        >
          Add
        </button>
      </form>
    </>
  );
}
export default Add;
