  const handlerSetName = (event) => {
    setName(event.target.value);
  };

  const handlerSetQuantity = (event) => {
    setQuantity(event.target.value);
  };

  const handlerSetPrice = (event) => {
    setPrice(event.target.value);
  };

  const handlerSetDiscount = (event) => {
    setDiscount(event.target.value);
  };
  // const handlerSubmitForm = () => {
  //   e.preventDefault();
  // };

  const navigate = useNavigate();
  return (
    <>
      <form>
        <label>Name:</label>
        <input type="text" name="name" placeholder="Enter Product Name"></input>
        <br />
        <label>Quantity:</label>
        <input type="text" name="quantity" placeholder="Enter Quantity"></input>
        <br />
        <label>Price:</label>
        <input type="text" name="price" placeholder="Enter Price"></input>
        <br />
        <label>Discount:</label>
        <input type="text" name="discount" placeholder="Enter Discount"></input>
        <br />
        <button
          onClick={(e) => {
            e.preventDefault();
            let { name, quantity, price, discount } = e.target;
            let myProduct = {
              id: 123,
              name: name.value,
              quantity: quantity.value,
              price: price.value,
              discount: discount.value,
            };
            handlerAdd(myProduct);
            // navigate("/view");
          }}
        >
          Add
        </button>
      </form>
    </>
  );
}
export default Add;
